// SearchUser.js
import { useState } from "react";
import { searchUsers, getUserRepositories } from "../api/api";

const SearchUser = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [repositories, setRepositories] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await searchUsers(searchQuery);
      setUsers(response.data.items);
    } catch (error) {
      setError("An error occurred while searching for users.");
    }
  };

  const handleUserClick = async (username) => {
    try {
      const response = await getUserRepositories(username);
      setRepositories(response.data);
      setSelectedUser(username);
    } catch (error) {
      setError(
        `An error occurred while fetching repositories for ${username}.`
      );
    }
  };

  return (
    <div className="h-screen p-36 bg-[#dfdfdf]">
      <div>
        <input
          type="text"
          className="block h-12 rounded-md p-2 w-full border-0  text-gray-900 placeholder-gray-500 focus:ring-0 sm:text-sm"
          placeholder="Insert Username"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <ul>
          <button
            onClick={handleSearch}
            type="button"
            className="inline-flex mt-3 w-full items-center rounded-md border border-transparent bg-indigo-600 px-6 py-3 text-base font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
          >
            SEARCH
          </button>
        </ul>
      </div>

      {error && <p>{error}</p>}

      <div className="mt-5">
        {users.map((user) => (
          <button
            className="inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-base font-medium text-gray-700 shadow-sm hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 w-full"
            key={user.id}
          >
            {user.login}
            <button className="justify-content-end" onClick={() => handleUserClick(user.login)}>
              View Repositories
            </button>
          </button>
        ))}
      </div>

      {selectedUser && (
        <div>
          <h2>{selectedUser}'s Repositories:</h2>
          <ul>
            {repositories.map((repo) => (
              <li key={repo.id}>{repo.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchUser;
