// SearchUser.js
import { useState } from "react";
import { searchUsers, getUserRepositories } from "../api/api";

const SearchUser = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [repositories, setRepositories] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await searchUsers(searchQuery);
      setUsers(response.data.items);
    } catch (error) {
      setError("An error occurred while searching for users.");
    }
  };

  const handleUserClick = async (username) => {
    try {
      const response = await getUserRepositories(username);
      setRepositories(response.data);
      setSelectedUser(username);
    } catch (error) {
      setError(
        `An error occurred while fetching repositories for ${username}.`
      );
    }
  };

  return (
    <div className=" p-20">
      <input
        type="text"
        className="block h-10 w-full border-0 p-0 text-gray-900 placeholder-gray-500 focus:ring-0 sm:text-sm"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <div>
        <button
          onClick={handleSearch}
          type="button"
          className="mt-4 w-20 inline-flex items-center rounded border border-transparent bg-indigo-600 px-2.5 py-1.5 text-xs font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
        >
          SEARCH
        </button>
      </div>

      {error && <p>{error}</p>}

      <div>
        {users.map((user) => (
          <li key={user.id}>
            {user.login}
            <button onClick={() => handleUserClick(user.login)}>
              View Repositories
            </button>
          </li>
        ))}
      </div>

      {selectedUser && (
        <div>
          <h2>{selectedUser}'s Repositories:</h2>
          <ul>
            {repositories.map((repo) => (
              <li key={repo.id}>{repo.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchUser;
